mapper.insert:
INSERT ALL
INTO tbl_member
(
	member_idx
	,member_id
	,member_pw
	,member_tel
	,member_lev
	,member_name
)
VALUES
(
	seq_member.nextval
	,#{memberId}
	,#{memberPw}
	,#{memberTel}
	,2
	,#{memberName}		
)
INTO tbl_seller
(
	sel_idx
	,member_idx
	,mar_idx
	,guname_marid
	,sel_type
	,sel_store
	,sel_address
	,sel_store_no
	,sel_info
	,sel_like			
	,sel_seller_loc
)
VALUES
(
	seq_seller.nextval
	,seq_member.currval
	,#{marIdx}
	,#{guNameMarId}		
	,#{selType}
	,#{selStore}
	,#{selAddress}
	,#{selStoreNo}
	,#{selInfo}	
	,0			
	,sdo_geometry(
		2001
		,8307
		,sdo_point_type(#{memberLNG}, #{memberLAT}, null)
		,null
		,null
	)
)
INTO tbl_seller_img
(
	sel_idx
	,sel_img
	,sel_img_uuid
)
VALUES
(
	seq_seller.currval
	,#{selImgOri}
	,#{selImgUUID}
)
SELECT * FROM DUAL

mapper.update:
UPDATE
	tbl_customer
SET
	customer_address=#{customerAddress}
WHERE
	member_idx=#{memberIdx}
