mapper.insert:
		INSERT INTO tbl_request
          ( REQUEST_IDX
           ,CUSTOMER_IDX
           ,REQUEST_TITLE
           ,REQUEST_ITEM
           ,REQUEST_MEMO
           ,REQUEST_RECEIVE_TIME
           ,GUNAME_MARID
           )
      VALUES
      		(
           SEQ_REQUEST.NEXTVAL  
           ,#{customerIdx}
           ,#{title}
           ,#{items}
           ,#{memo}
           ,#{receiveTime}
           ,#{guName}||#{marketName}
           )

mapper.update:
 	
 		UPDATE  TBL_REQUEST
		SET 	REQUEST_DELETE_FLAG = 0
		WHERE 	REQUEST_IDX = #{idx}

 		update tbl_request
        set    REQUEST_TITLE = #{title}
	          ,REQUEST_ITEM = #{items}
	          ,REQUEST_MEMO = #{memo}
	          ,REQUEST_RECEIVE_TIME = #{receiveTime}
	          ,GUNAME_MARID = #{guName}||#{marketName}
       where   REQUEST_IDX = #{idx}

mapper.insert:
 		INSERT INTO 
 		TBL_REQUEST_REPLY ( REQUEST_REPLY_IDX
						   ,REQUEST_IDX
						   ,SEL_IDX
						   ,REQUEST_REPLY_REPLY
 						  )
		VALUES ( SEQ_TBL_REQUEST_REPLY.NEXTVAL
		        ,#{requestIdx}
		        ,#{selIdx}
		        ,#{replys}
		       )

mapper.update:
		update tbl_request
       set    REPLY_CNT =REPLY_CNT + 1
      where   REQUEST_IDX = #{idx}

		update tbl_request
       set    REPLY_CNT =REPLY_CNT - 1
      where   REQUEST_IDX = #{idx}

		update TBL_REQUEST_REPLY
       set    REQUEST_DELETE_REPLY_FLAG = 0
      where   REQUEST_REPLY_IDX = #{idx}

 		
		update TBL_REQUEST_REPLY
        set    REQUEST_REPLY_REPLY = #{replys}
       where   REQUEST_REPLY_IDX = #{replyIdx}

 		
		update TBL_REQUEST_REPLY
        set    REQUEST_REPLY_FLAG = 2
       where   REQUEST_REPLY_IDX = #{replyIdx}

 		
		update TBL_REQUEST
